tinymce.PluginManager.add('testimonial', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('testimonial', {
        text: 'testimonial',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Testimonial',
                body: [
                    {type: 'textbox', name: 'title', label: 'Title'},
                    {type: 'textbox', name: 'title', label: 'Title'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('Title: ' + e.data.title);
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('testimonial', {
        text: 'Testimonial',
        context: 'insert',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'testimonial',
                url: '/testimonials',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});